import { StyleSheet, css } from "aphrodite";
import React, { useState, useEffect } from "react";
import { Form } from "semantic-ui-react";
import Ghub from "../ghub.png";
import Web from "../web.png";
import Twt from "../twitter.png";
// import { Image } from "semantic-ui-react";

const Home = () => {
	const [name, setName] = useState("");
	const [userName, setUserName] = useState("");
	const [followers, setFollowers] = useState("");
	const [following, setFollowing] = useState("");
	const [repos, setRepos] = useState("");
	const [avatar, setAvatar] = useState("");
	const [userInput, setUserInput] = useState("");
	const [bio, setBio] = useState("");
	const [tuname, setTuname] = useState("");
	const [email, setEmail] = useState("");
	const [website, setWebsite] = useState("");
	const [error, setError] = useState(null);

	useEffect(() => {
		fetch("https://api.github.com/users/shradzz00")
			.then((res) => res.json())
			.then((data) => {
				// console.log(data);
				setData(data);
			});
	}, []);

	const setData = ({
		name,
		login,
		followers,
		following,
		public_repos,
		avatar_url,
		bio,
		twitter_username,
		blog,
		email,
	}) => {
		setName(name);
		setUserName(login);
		setFollowers(followers);
		setFollowing(following);
		setRepos(public_repos);
		setAvatar(avatar_url);
		setBio(bio);
		setTuname(twitter_username);
		setWebsite(blog);
		setEmail(email);
	};

	const handleSearch = (e) => {
		setUserInput(e.target.value);
	};

	const handleSubmit = () => {
		fetch(`https://api.github.com/users/${userInput}`)
			.then((res) => res.json())
			.then((data) => {
				if (data.message) {
					setError("No user found");
				} else {
					setData(data);
					setError(null);
				}
			});
	};

	return (
		<div className={css(styles.root)}>
			{/* <div className={css(styles.search)}>
				<Form onSubmit={handleSubmit}>
					<Form.Group>
						<Form.Input onChange={handleSearch} placeholder="Github userame" />

						<Form.Button content="Submit" />
					</Form.Group>
				</Form>
			</div> */}

			{error ? (
				<h1>{error}</h1>
			) : (
				// <div>
				// 	<div>
				// 		<img src={avatar} alt="avatar" className={css(styles.img)} />
				// 	</div>
				// 	<h3>{name}</h3>
				// 	<br />
				// 	<h4>{userName}</h4>
				// 	<br />
				// 	<p> {followers} </p>
				// 	<br />
				// 	<span> {following} </span>
				// 	<br />
				// 	<span> {repos} </span>
				// 	<br />
				// 	<span>{bio}</span>
				// 	<br />
				// 	<span>{tuname}</span>
				// 	<br />
				// 	<span>{website} </span>
				// 	<br />
				// 	<span>{email}</span>
				// </div>
				<div className={css(styles.container)}>
					<div className={css(styles.leftContainer)}>
						<div className={css(styles.leftContents)}>
							<div className={css(styles.imgsec)}>
								<div>
									<img
										src={avatar}
										alt="avatar"
										className={css(styles.avatar)}
									/>
								</div>
								<span className={css(styles.uname)}>sawdwad</span>
							</div>
							<div className={css(styles.socials)}>
								<div className={css(styles.socialIcons)}>
									<img src={Web} alt="" className={css(styles.iconStyle)} />
									<span className={css(styles.desc)}>wdwadwd.com</span>
								</div>
								<div className={css(styles.socialicons)}>
									<img src={Ghub} alt="" className={css(styles.iconStyle)} />
									<span className={css(styles.desc)}>wdwadwd.com</span>
								</div>
								<div className={css(styles.socialicons)}>
									<img src={Twt} alt="" className={css(styles.iconStyle)} />
									<span className={css(styles.desc)}>wdwadwd.com</span>
								</div>
							</div>
							<button className={css(styles.button)}>
								<img src="" alt="" className={css(styles.buttonimg)} />
							</button>
						</div>
					</div>

					{/* Right section */}
					<div className={css(styles.rightContainer)}>
						<div className={css(styles.rightContents)}>
							<div className={css(styles.nameSection)}>
								<span className={css(styles.fullName)}>awdwahdg bDuha</span>
								<div className={css(styles.divider)}></div>
							</div>

							{/* bio Sectiom */}

							<div className={css(styles.bioSection)}>
								<span className={css(styles.bio)}>Bio</span>
								<div className={css(styles.bioCard)}>
									<span className={css(styles.bioContent)}>
										wdawduwdhkjawdkjb awdiuwa dwadhuwj awduhdakjd awjdhj
									</span>
								</div>
							</div>

							{/* Status section */}

							<div className={css(styles.statusSection)}>
								<div className={css(styles.statusCard)}>
									<div className={css(styles.circle)}>
										<span className={css(styles.statusNo)}>43</span>
									</div>
									<span className={css(styles.statusText)}>followers</span>
								</div>
								<div className={css(styles.statusCard)}>
									<div className={css(styles.circle)}>
										<span className={css(styles.statusNo)}>43</span>
									</div>
									<span className={css(styles.statusText)}>followers</span>
								</div>
								<div className={css(styles.statusCard)}>
									<div className={css(styles.circle)}>
										<span className={css(styles.statusNo)}>43</span>
									</div>
									<span className={css(styles.statusText)}>followers</span>
								</div>
							</div>
						</div>
					</div>
				</div>
			)}
		</div>
	);
};

const styles = StyleSheet.create({
	root: {
		textAlign: "center",
		display: "flex",
		justifyContent: "center",
		alignItems: "center",
	},
	// img: {
	// 	width: 100,
	// 	height: 100,
	// },

	container: {
		marginTop: 30,
		height: 850,
		width: 1200,
		borderRadius: 15,
		background:
			"linear-gradient(to right, rgba(255, 255, 255, 0.7), rgba(255, 255, 255, 0.3));",
	},
	leftContainer: {
		marginTop: "0px !important",
		textAlign: "left",
		width: 414,
		height: 850,
		borderRadius: 15,
		background:
			"linear-gradient(141.57deg, rgba(255, 255, 255, 0.7) 0.88%, rgba(255, 255, 255, 0.3) 77.88%)",
	},
	leftContents: {
		border: "1px solid black",
		marginLeft: 23,
		marginRight: 23,
		width: 368,
		height: 640,
		paddingTop: 71,
	},
	imgsec: {
		display: "flex",
		flexDirection: "column",
		marginLeft: "auto",
		marginRight: "auto",
		textAlign: "center",
	},
	avatar: {
		width: 200,
		height: 200,
		borderRadius: "50%",
	},
	uname: {
		fontWeight: "bold",
		fontSize: 28,
		lineHeight: "34px",
		fontStyle: "normal",
		color: "#0A4A58",
	},
	socials: {
		border: "1px solid black",
		minHeight: 205,
	},
	socialIcons: {
		display: "flex",
		flexDirection: "row",
		justifyContent: "flex-start",
	},
	iconStyle: {
		width: 40,
		height: 40,
	},
	desc: {
		fontWeight: 500,
		fontSize: 24,
		lineHeight: "29px",
		fontStyle: "normal",
		marginLeft: 20,
		color: "#0A4A58",
	},
});

export default Home;
